package cz.cuni.lf1.thunderstorm.algorithms.detectors

import cz.cuni.lf1.thunderstorm.datastructures.extensions.watershed.WatershedAlgorithm
import cz.cuni.lf1.thunderstorm.datastructures.extensions.watershed.toByteProcessor
import cz.cuni.lf1.thunderstorm.datastructures.extensions.watershed.toGrayScaleImage
import cz.cuni.lf1.thunderstorm.test.assert2DDoubleArrayEquals
import ij.ImagePlus
import ij.plugin.filter.MaximumFinder
import ij.process.FloatProcessor
import ij.process.ImageProcessor
import org.junit.Test

internal class WatershedTest {

    @Test
    public fun test() {
        val fp = FloatProcessor(64, 64, binaryIntImage.flatMap { it.map { (it * 255).toFloat() } }.toFloatArray())
        val watershedImageOrig = ImageJ_MaximumFinder().findMaxima(fp, 0.0, ImageProcessor.NO_THRESHOLD, MaximumFinder.SEGMENTED, false, false)
        val watershedImageNew = WatershedAlgorithm.findMaxima(fp.toGrayScaleImage()).toByteProcessor()

        ImagePlus("fp", fp).show()
        ImagePlus("orig", watershedImageOrig).show()
        ImagePlus("new", watershedImageNew).show()

        Thread.sleep(5000)

        assert2DDoubleArrayEquals(
                arrayOf((watershedImageOrig.pixels as ByteArray).map(Byte::toDouble).toTypedArray()),
                arrayOf((watershedImageNew.pixels as ByteArray).map(Byte::toDouble).toTypedArray()), 0.0)
    }
}

